examples.technicalQuery:
query WeeklyPerformance($where: PacingDataObjectWhereInput, $first: Int) {
  pacingDataObjectsConnection(where: $where, first: $first) {
    edges {
      node {
        id
        date
        impressions
        clicks
        spend
        revenue
        ctr
        cpm
      }
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}

